import{debounce}from"./debounce.js";const lookForParentScrollableElement=t=>{const e=t.parentNode;return e===document.body?window:e.scrollHeight>e.clientHeight?e:lookForParentScrollableElement(e)};export const trackVisibility=(t,e)=>{lookForParentScrollableElement(t).onscroll=debounce((()=>{e(isInViewport(t))}),-250),e(isInViewport(t))};export const isInViewport=function(t){const e=t.getBoundingClientRect();return e.top||e.left||e.bottom||e.right||e.width||e.height?e.top>=0&&e.left>=0&&e.bottom<=(window.innerHeight||document.documentElement.clientHeight)&&e.right<=(window.innerWidth||document.documentElement.clientWidth):null};
